import styled from "styled-components";

export const Header = styled.header `
  height: 5vh;
  background: grey;
  top: 0;
  position: sticky;
`;

export const Main = styled.main `
  margin: 10px 20px;
  height: 90vh;
  overflow: hidden;
  padding: 20px;
  display: flex;
  justify-content: center;
`;

export const Footer = styled.footer `
  display: flex;
  background: black;
  color: white;
  padding: 5px;
  justify-content: center;
`;
